<template>
  <section class="form">
    <div class="input-group">
      <input
        required
        type="text"
        name="text"
        autocomplete="off"
        class="input"
      />
      <label class="user-label">Nombre</label>
    </div>
    <div class="input-group">
      <input
        required
        type="text"
        name="text"
        autocomplete="off"
        class="input"
      />
      <label class="user-label">¿De dónde eres?</label>
    </div>
    <div @click="takePicture" class="upload-image"></div>
  </section>
</template>

<script lang="ts">
import { defineComponent } from "vue";
import { Camera, CameraResultType } from "@capacitor/camera";
import { uploadFile, getFilePath } from "@/services/supabase.service";
export default defineComponent({
  name: "OnboardingForm",
  data() {
    return {
      photo: null,
      userData: sessionStorage.getItem("user-data"),
    };
  },
  watch: {
    photo: function (newPhoto) {
      if (newPhoto) {
        const picture = document.querySelector(".upload-image") as HTMLElement;
        picture.style.backgroundImage = `url(${newPhoto})`;
      }
    },
  },
  methods: {
    async takePicture() {
      let userId = JSON.parse(this.userData).id;
      const image = await Camera.getPhoto({
        quality: 90,
        allowEditing: true,
        resultType: CameraResultType.Base64,
      });
      this.photo = await uploadFile(
        image.base64String,
        "images/avatars",
        `avatar-${userId}`,
        userId
      );
    },
  },
});
</script>

